/*global app*/
var templates = require('templates');
var BaseView = require('people/base-view');
var PersonForm = require('people/forms/person-form');

module.exports = BaseView.extend({
  pageTitle: 'add person',
  template: templates.personAdd,
  subviews: {
    form: {
      container: 'form',
      prepareView: function(el) {
        return new PersonForm({
          el: el,
          submitCallback: function(data) {
            app.people.create(data, {
              wait: true,
              success: function() {
                app.navigate('/people');
                app.people.fetch();
              }
            });
          }
        });
      }
    }
  }
});
