/*global app, alert*/
var templates = require('templates');
var BaseView = require('people/base-view');
var PersonForm = require('people/forms/person-form');

module.exports = BaseView.extend({
  pageTitle: 'view person',
  template: templates.personView,
  bindings: {
    'model.fullName': {
      hook: 'name'
    },
    'model.avatar': {
      type: 'attribute',
      hook: 'avatar',
      name: 'src'
    },
    'model.editUrl': {
      type: 'attribute',
      hook: 'edit',
      name: 'href'
    }
  },
  events: {
    'click [data-hook~=delete]': 'handleDeleteClick'
  },
  initialize: function(spec) {
    var self = this;
    app.people.getOrFetch(spec.id, {
      all: true
    }, function(err, model) {
      if (err) alert('couldnt find a model with id: ' + spec.id);
      self.model = model;
    });
  },
  handleDeleteClick: function() {
    this.model.destroy({
      success: function() {
        app.navigate('people');
      }
    });
  }
});
