var Router = require('ampersand-router');
var HomePage = require('home');
var InfoPage = require('info');
var PeoplePage = require('people');
var PersonAddPage = require('people/person-add');
var PersonEditPage = require('people/person-edit');
var PersonShowPage = require('people/person-show');

module.exports = Router.extend({
  routes: {
    '': 'home',
    'info': 'info',
    'people': 'people',
    'person/add': 'personAdd',
    'person/:id': 'personShow',
    'person/:id/edit': 'personEdit',
    '(*path)': 'catchAll'
  },

  home: function() {
    this.trigger('page', new HomePage());
  },

  info: function() {
    this.trigger('page', new InfoPage());
  },

  people: function() {
    this.trigger('page', new PeoplePage({
      collection: app.people
    }));
  },

  personAdd: function() {
    this.trigger('page', new PersonAddPage());
  },

  personEdit: function(id) {
    this.trigger('page', new PersonEditPage({
      id: id
    }));
  },

  personShow: function(id) {
    this.trigger('page', new PersonShowPage({
      id: id
    }));
  },

  catchAll: function() {
    this.redirectTo('');
  }
});
